mapper.select:
SELECT *
FROM _inquiry
ORDER BY created_at DESC
LIMIT #{limit}
OFFSET #{offset}

mapper.insert:
INSERT INTO _inquiry (
member_id, title, content, istatus, created_at, image_id, attachment, status,
email, phone
) VALUES (
#{member_id}, #{title}, #{content}, #{istatus}, NOW(), #{image_id}, #{attachment},
#{status}, #{email}, #{phone}
)
