mapper.select:
select * 
from _notice
where status = #{status}

    and noCategory = #{category}


    and (
        noTitle like concat('%', #{keyword}, '%')
        or noContent like concat('%', #{keyword}, '%')
    )

order by 
    important desc,
    noRegdate desc
limit #{start}, #{size}

select count(*) 
from _notice
where status = #{status}

    and noCategory = #{category}


    and (
        noTitle like concat('%', #{keyword}, '%')
        or noContent like concat('%', #{keyword}, '%')
    )

mapper.update:
update _notice 
set viewCount = viewCount + 1 
where noId = #{noId}

mapper.select:
select * from _notice_file
where noId = #{noId}
order by fileType desc, uploadDate asc

select * from _notice_file
where noId = #{noId}
and fileType = #{fileType}
order by uploadDate asc

mapper.delete:
delete from _notice_file 
where noId = #{noId} 
and fileType = #{fileType}
