mapper.select:
select * 
from notice
where status = #{status}

    and noCategory = #{category}


    and (
        noTitle like concat('%', #{keyword}, '%')
        or noContent like concat('%', #{keyword}, '%')
    )

order by 
    important desc,
    noRegdate desc
limit #{start}, #{size}

select count(*) 
from notice
where status = #{status}

    and noCategory = #{category}


    and (
        noTitle like concat('%', #{keyword}, '%')
        or noContent like concat('%', #{keyword}, '%')
    )

mapper.update:
update notice 
set viewCount = viewCount + 1 
where noId = #{noId}
